const useFetch = (setTasks, setError, setIsLoading) => {
    setIsLoading(true);
    setError(null);

    fetch('https://custom-hooks-e72bb-default-rtdb.firebaseio.com/tasks.json')
        .then((response) => {
            if (!response.ok) {
                throw new Error('Request failed!');
            }

            return response.json();
        })
        .then((data) => {
            const loadedTasks = [];
            for (const taskKey in data) {
                loadedTasks.push({ id: taskKey, text: data[taskKey].text });
            }

            setTasks(loadedTasks);
        })
        .catch((err) => {
            setError(err.message || 'Something went wrong!');
        });

    setIsLoading(false);
};

export default useFetch;
