import Section from '../UI/Section';
import TaskForm from './TaskForm';
import useFetch from '../../hooks/useFetch';

const NewTask = (props) => {
    const array = [];

    const EnterTaskHandler = (taskText) => {
        const request = {
            parameter: true,
            taskText,
            onAddTask: props.onAddTask,
        };

        const { isLoading, error } = useFetch(request);

        array = [isLoading, error];
    };

    return (
        <Section>
            <TaskForm onEnterTask={EnterTaskHandler} loading={array[0]} />
            {array[1] && <p>{array[1]}</p>}
        </Section>
    );
};

export default NewTask;
