const useFetch = (parameter, setError, setIsLoading, setTasks, taskText) => {
    setIsLoading(true);
    setError(null);
    let createdTask = null;
    let content = null;

    if (parameter) {
        content =
            ('https://custom-hooks-e72bb-default-rtdb.firebaseio.com/tasks.json',
            {
                method: 'POST',
                body: JSON.stringify({ text: taskText }),
                headers: {
                    'Content-Type': 'application/json',
                },
            });
    } else {
        content = 'https://custom-hooks-e72bb-default-rtdb.firebaseio.com/tasks.json';
    }

    fetch(content)
        .then((response) => {
            if (!response.ok) {
                throw new Error('Request failed!');
            }

            return response.json();
        })
        .then((data) => {
            if (!parameter) {
                const loadedTasks = [];
                for (const taskKey in data) {
                    loadedTasks.push({ id: taskKey, text: data[taskKey].text });
                }

                setTasks(loadedTasks);
            } else {
                const generatedId = data.name;
                createdTask = { id: generatedId, text: taskText };
            }
        })
        .catch((err) => {
            setError(err.message || 'Something went wrong!');
        });

    setIsLoading(false);
    return createdTask;
};

export default useFetch;
